import React, { useState, useEffect } from 'react';
import socketIOClient from 'socket.io-client';

import GameSessionSocketContext from '../context/GameSessionSocketContext';
import ClickableAreaWithSocket from './components/ClickableArea';

export default function GameSession() {
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [connectedToSocket, setConnectedToSocket] = useState(false);
  const [socket, setSocket] = useState(false);

  useEffect(() => {
    setLoading(true);

    setSocket(socketIOClient("http://127.0.0.1:6969"));

    socket.on('connect_failed', () => {
      setLoading(false);
      setError({ message: "Could not connect to server." });
    });

    // will need to handle setting global state for returned Tower object with all info
    socket.on("FromAPI", (data) => {
      setLoading(false);
      console.log(data);
    });

    // handle component unload
    return () => { socket.disconnect(); };
  });

  if (error) { return <div>{error.message}</div>; };
  if (loading) { return <div>Loading...</div>; }

  return (
    <div style={{ textAlign: "center" }}>
      <GameSessionSocketContext.Provider value={socket} >
        <ClickableAreaWithSocket />
      </GameSessionSocketContext.Provider>
    </div>
  );
}
