import React, { useState } from 'react';
import { connect } from 'react-redux';

import { connectToSocket } from '../actions/socketActions';
import { setCurrentScreen } from '../actions/applicationStateActions';

function Signup(props) {
  // const { connectToSocket, setCurrentScreen } = props;

  // already signed in (grab from redux) - logout
  // ready to signup
  // waiting on signup result
  // successfully signed up - play game

  const [signupStep, setSignupStep] = useState('SignupForm');

  const handleStartNewGame = () => {
    setCurrentScreen('GameSession');
    connectToSocket();
  }

  const signupForm = () => {
    return (
      <div>Sign Up Form</div>
    );
  }

  const signupSuccess = () => {
    return (
      <div style={{ textAlign: "center" }}>
        <div>Congrats, your user is ready!</div>
        <div>Signed in as: USER EMAIL GOES HERE</div>
        <button onClick={handleStartNewGame}>Start New Game</button>
      </div>
    );
  }

  const renderSignupStep = () => {
    let toRender;

    switch(signupStep) {
      case 'SignupForm':
        toRender = signupForm();
        break;
      case 'Loading':
        toRender = signupLoading();
        break;
      case 'Error':
        toRender = signupError();
        break;
      case 'SignupSuccess':
        toRender = signupSuccess();
        break;
      default:
        toRender = <div>Error</div>;
    }

    return toRender;
  }

  return renderSignupStep();
}

function mapStateToProps(state) {
  return {
    socket: state.socket
  };
}

function mapDispatchToProps(dispatch) {
  return {
    connectToSocket: connectToSocket(dispatch),
    setCurrentScreen: setCurrentScreen(dispatch)
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Signup);
