import React, { useState } from 'react';
import { connect } from 'react-redux';

import { connectToSocket } from '../actions/socketActions';
import { setCurrentScreen } from '../actions/applicationStateActions';

function Signup(props) {
  // const { connectToSocket, setCurrentScreen } = props;

  // already signed in (grab from redux) - logout
  // ready to signup
  // waiting on signup result
  // successfully signed up - play game

  const [signupStep, setSignupStep] = useState('SignupForm');

  const renderSignupStep = () => {
    let toRender;

    switch(props.applicationState.currentScreen) {
      case 'LandingPage':
        toRender = <LandingPage />;
        break;
      case 'GameSession':
        toRender = <GameSession />;
        break;
      // case 'Login':
      //   toRender = <Login />;
      //   break;
      case 'Signup':
        toRender = <Signup />;
        break;
      default:
        toRender = <div>Error</div>;
    }

    return toRender;
  }

  const handleStartNewGame = () => {
    setCurrentScreen('GameSession');
    connectToSocket();
  }

  return (
    <div style={{ textAlign: "center" }}>
      <button onClick={handleStartNewGame}>Start New Game</button>
      <button>Login</button>
    </div>
  );

}

function mapStateToProps(state) {
  return {
    socket: state.socket
  };
}

function mapDispatchToProps(dispatch) {
  return {
    connectToSocket: connectToSocket(dispatch),
    setCurrentScreen: setCurrentScreen(dispatch)
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Signup);
