import {
  SOCKET_TRIGGER_CONNECT, SOCKET_CONNECT_ERROR, SOCKET_ERROR,
  SOCKET_TIMEOUT, SOCKET_CONNECT, SOCKET_DISCONNECT,
  SOCKET_TRIGGER_DISCONNECT, SOCKET_SET_LOADING
} from './actionTypes';

export function connectToSocket(dispatch) {
  return () => {
    dispatch({ type: SOCKET_TRIGGER_CONNECT });
  }
}

export function setSocketLoading(dispatch, loading) {
    dispatch({
      type: SOCKET_SET_LOADING,
      payload: loading
    });
}

export function handleSocketConnect(dispatch, data) {
  return () => {
    dispatch({
      type: SOCKET_CONNECT,
      payload: data
    });
  }
}
