import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import socketIOClient from 'socket.io-client';

import { connectToSocket } from '../actions/socketActions';
import ClickableArea from './ClickableArea';

let socket;

function GameSession(props) {
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [connectedToSocket, setConnectedToSocket] = useState(false);

  useEffect(() => {
    console.log('render')
    socket = socket || socketIOClient("http://127.0.0.1:6969");

    console.log("socket.connected")
    console.log(socket.connected)

    socket.on('connect', (data) => {
      // handle hydration of game state from this data
      console.log("SOCKET_CONNECT");
    });

    socket.on('disconnect', (timeout) => {
      console.log("SOCKET_DISCONNECT");
    });

    socket.on("FromAPI", (message) => {
      console.log(message);
      console.log("FromAPI")
    });
  });

  if (error) { return <div>{error.message}</div>; };
  if (loading) { return <div>Loading...</div>; }

  return (
    <div style={{ textAlign: "center" }}>
      <div onClick={props.connectToSocket}>loooooool</div>
      <ClickableArea />
    </div>
  );
}

function mapStateToProps(state) {
  return {
    count: state.count,
    stuff: state
  };
}

function mapDispatchToProps(dispatch) {
  return {
    connectToSocket: () => dispatch({ type: "SOCKET_TRIGGER_CONNECT" })
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(GameSession);
