import React, { useState } from 'react';

import GameSessionSocketContext from '../context/GameSessionSocketContext';

const ClickableArea = (props) => {
  // Declare a new state variable, which we'll call "count"
  const [count, setCount] = useState(0);

  const socket = props.socket;

  const handleClick = () => {
    setCount(count + 1);
    socket.emit('areaClicked', 'yep');
  }

  return (
    <div>
      <p>You clicked {count} times</p>
      <button onClick={handleClick}>
        Click me
      </button>
    </div>
  );
}

export default function ClickableAreaWithSocket(props) {
  return (
    <GameSessionSocketContext.Consumer>
      {socket => <ClickableArea {...props} socket={socket} />}
    </GameSessionSocketContext.Consumer>
  );
}
